import { Component } from 'react/lib/React'
import Chip from 'Chip'

export default class CalendariesCloud extends Component {
   static defaultProps = {
      calendaries: [],
      calendaries_used: [],
      onAct: null,
   }

   onChipAct = (data) => {
      this.props.onAct(data)
   }

   isCalendaryUsed = (calendary) => {
      return this.props.calendaries_used.reduce((used, c) => {
         used || (c.slug == calendary.slug)
      })
   }

   actionFor = (calendary) => {
      return this.isCalendaryUsed(calendary) ? null : 'add'
   }

   render = () => {
      return (
         <div className='row centering'>
            {this.props.calendaries.map((calendary) =>
               <Chip
                  key={calendary.slug}
                  data={{slug: calendary.slug}}
                  className='calendary'
                  text={calendary.name}
                  color={calendary.color}
                  url={calendary.url}
                  action={this.actionFor(calendary)}
                  onAct={this.onChipAct} />)}
         </div>)}}
